(1489, 'document', 'text/html', 'Industria alimentaria', '', '', 'industria-alimentaria', '', 1, 0, 0, 1454, 1, '', '<p><span lang="es-ES">La industria alimenticia es una extensa área que incluye tanto el procesamiento de alimentos primarios como la elaboración de productos semiterminados o listos para el consumo.</span></p>\r\n<p><span lang="es-ES">El lavado en la industria alimentaria debe ser eficaz, seguro y económico.</span></p>\r\n<p><span lang="es-ES">El nivel de limpieza e higiene en la empresa se refleja esencialmente en la calidad del producto final.</span></p>\r\n<p> </p>\r\n<p>Procesad de carne</p>\r\n<ul class="list3">\r\n<li>Desinfección de superficies</li>\r\n<li>Lavado de equipos térmicos</li>\r\n<li>Limpieza in situ (CIP)</li>\r\n<li>Lavado de equipos de alimentación</li>\r\n<li>Lavado de instalaciones</li>\r\n<li>Lavado de cámaras frigoríficas</li>\r\n<li>Lavado de túnel</li>\r\n<li>Limpieza de lugares comunes</li>\r\n<li>Higiene de personal</li>\r\n<li>Herramientas de eliminación de obstrucciones en tubería</li>\r\n</ul>\r\n<p>La industria láctea</p>\r\n<ul class="list3">\r\n<li>Limpieza in situ (CIP)</li>\r\n<li>Lavado de equipos de alimentación</li>\r\n<li>Desinfección de superficies</li>\r\n<li>Lavado de áreas de produccion</li>\r\n<li>Túnel de lavado</li>\r\n<li>Lavado de cámaras frigoríficas</li>\r\n<li>Limpieza de lugares comunes</li>\r\n<li>Higiene personal</li>\r\n<li>Herramientas de eliminación de obstrucciones en tuberías</li>\r\n</ul>\r\n<p>Cerveza y bebidas</p>\r\n<ul class="list3">\r\n<li>Limpieza in situ (CIP)</li>\r\n<li>Lavado de equipos de alimentación</li>\r\n<li>Desinfección de superficies</li>\r\n<li>Lavado de áreas de producción</li>\r\n<li>Lavado de túnel</li>\r\n<li>Higiene personal</li>\r\n<li>Herramientas de eliminación de obstrucciones en tuberías</li>\r\n</ul>\r\n<p>Pan y pastelería</p>\r\n<ul class="list3">\r\n<li>Desinfección de superficies</li>\r\n<li>Lavado de equipos térmicos</li>\r\n<li>Lavado de equipos de alimentación</li>\r\n<li>Lavado de áreas de producción</li>\r\n<li>Lavado de túnel</li>\r\n<li>Lavado de cámaras frigoríficas</li>\r\n<li>Limpieza de lugares comunes</li>\r\n<li>Higiene personal</li>\r\n<li>Herramientas de eliminación de obstrucciones en tuberías</li>\r\n</ul>\r\n<p>Producción de salsas</p>\r\n<ul class="list3">\r\n<li>Limpieza in situ (CIP)</li>\r\n<li>Lavado de equipos de alimentación</li>\r\n<li>Desinfección de superficies</li>\r\n<li>Lavado de áreas de producción</li>\r\n<li>Lavado de túnel</li>\r\n<li>Higiene personal</li>\r\n<li>Herramientas de eliminación de obstrucciones en tuberías</li>\r\n</ul>\r\n<p>Industria de aseites y grasas</p>\r\n<ul class="list3">\r\n<li>Limpieza in situ (CIP)</li>\r\n<li>Lavado de equipos de alimentación</li>\r\n<li>Desinfección de superficies</li>\r\n<li>Lavado de áreas de producción</li>\r\n<li>Lavado de túnel</li>\r\n<li>Higiene personal</li>\r\n<li>Herramientas de eliminación de obstrucciones en tuberías</li>\r\n</ul>\r\n', 1, 16, 13, 1, 1, 94, 1488484261, 94, 1488819356, 0, 0, 0, 1386590580, 1, '', 0, 0, 0, 0, 0, 'modDocument', 'es', 1, 'cataloges/industria-alimentaria.html', 0, 0, 1, NULL),
