(33, 1, 0, 'hookComEmail', '', 0, 0, 0, '$message = ''\n<p>Спасибо за регистрацию! В ближайшее время наш менеджер свяжется с вами.</p>\n<p><em>Администрация сайта specsintez.com</em></p>'';\n\n\n\n$modx->getService(''mail'', ''mail.modPHPMailer'');\n$modx->mail->set(modMail::MAIL_BODY,$message);\n\n$modx->mail->set(modMail::MAIL_FROM,''specsin@user.z8.ru'');\n//$modx->mail->set(modMail::MAIL_FROM,''mail@specsintez.com'');\n$modx->mail->set(modMail::MAIL_FROM_NAME,''СпецСинтез'');\n$modx->mail->set(modMail::MAIL_SENDER,''specsin@user.z8.ru'');\n$modx->mail->set(modMail::MAIL_SUBJECT,''Спасибо за регистрацию'');\n\n$modx->mail->address(''to'',$hook->getValue(''email''));\n//$modx->mail->IsMail();\n$modx->mail->setHTML(true);\nif (!$modx->mail->send()) {\n    $modx->log(modX::LOG_LEVEL_ERROR,''EmailTo:''.$hook->getValue(''email'').''-An error occurred while trying to send the email: ''.$modx->mail->mailer->ErrorInfo);\n}\n$modx->mail->reset();\nreturn true;', 0, 'a:0:{}', '', 0, ''),
(34, 1, 0, 'MinifyX', 'MinifyX is a snippet the allows you to combine and minify JS and CSS files', 0, 18, 0, '/** @var array $scriptProperties */\nif (!$modx->getService(''minifyx'',''MinifyX'', MODX_CORE_PATH.''components/minifyx/model/minifyx/'')) {return;}\n/** @var MinifyX $MinifyX */\n$MinifyX = new MinifyX($modx, $scriptProperties);\nif (!$MinifyX->prepareCacheFolder()) {\n	$modx->log(modX::LOG_LEVEL_ERROR, ''[MinifyX] Could not create cache dir "''.$MinifyX->config[''cacheFolder''].''"'');\n	return;\n}\n$cacheFolderUrl = MODX_BASE_URL . str_replace(MODX_BASE_PATH, '''', $MinifyX->config[''cacheFolder'']);\n\n$array = array(\n	''js'' => trim($modx->getOption(''jsSources'', $scriptProperties, '''', true)),\n	''css'' => trim($modx->getOption(''cssSources'', $scriptProperties, '''', true)),\n);\n\nforeach ($array as $type => $value) {\n	if (empty($value)) {continue;}\n	$filename = $MinifyX->config[$type.''Filename''] . ''_'';\n	$extension = $MinifyX->config[$type.''Ext''];\n	$register = $MinifyX->config[''register''.ucfirst($type)];\n	$placeholder = !empty($MinifyX->config[$type.''Placeholder''])\n		? $MinifyX->config[$type.''Placeholder'']\n		: '''';\n\n	$files = $MinifyX->prepareFiles($value);\n	$properties = array(\n		''minify'' => $MinifyX->config[''minify''.ucfirst($type)]\n				? ''true''\n				: ''false'',\n	);\n\n	$result = $MinifyX->Munee($files, $properties);\n	$file = $MinifyX->saveFile($result, $filename, $extension);\n\n	// Register file on frontend\n	if (!empty($file) && file_exists($MinifyX->config[''cacheFolder''] . $file)) {\n		if ($register == ''placeholder'' && $placeholder) {\n			$tag = $type == ''css''\n				? ''<link rel="stylesheet" href="'' . $cacheFolderUrl .  $file . ''" type="text/css" />''\n				: ''<script type="text/javascript" src="'' . $cacheFolderUrl . $file . ''"></script>'';\n			$modx->setPlaceholder($placeholder, $tag);\n		}\n		else {\n			if ($type == ''css'') {\n				$modx->regClientCSS($cacheFolderUrl . $file);\n			}\n			else {\n				if ($register == ''startup'') {\n					$modx->regClientStartupScript($cacheFolderUrl . $file);\n				}\n				else {\n					$modx->regClientScript($cacheFolderUrl . $file);\n				}\n			}\n		}\n	}\n}\nreturn;', 0, 'a:10:{s:9:"jsSources";a:7:{s:4:"name";s:9:"jsSources";s:4:"desc";s:22:"minifyx_prop_jsSources";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:0:"";s:7:"lexicon";s:18:"minifyx:properties";s:4:"area";s:0:"";}s:10:"cssSources";a:7:{s:4:"name";s:10:"cssSources";s:4:"desc";s:23:"minifyx_prop_cssSources";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:0:"";s:7:"lexicon";s:18:"minifyx:properties";s:4:"area";s:0:"";}s:8:"minifyJs";a:7:{s:4:"name";s:8:"minifyJs";s:4:"desc";s:21:"minifyx_prop_minifyJs";s:4:"type";s:13:"combo-boolean";s:7:"options";a:0:{}s:5:"value";b:0;s:7:"lexicon";s:18:"minifyx:properties";s:4:"area";s:0:"";}s:9:"minifyCss";a:7:{s:4:"name";s:9:"minifyCss";s:4:"desc";s:22:"minifyx_prop_minifyCss";s:4:"type";s:13:"combo-boolean";s:7:"options";a:0:{}s:5:"value";b:0;s:7:"lexicon";s:18:"minifyx:properties";s:4:"area";s:0:"";}s:10:"jsFilename";a:7:{s:4:"name";s:10:"jsFilename";s:4:"desc";s:23:"minifyx_prop_jsFilename";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:7:"scripts";s:7:"lexicon";s:18:"minifyx:properties";s:4:"area";s:0:"";}s:11:"cssFilename";a:7:{s:4:"name";s:11:"cssFilename";s:4:"desc";s:24:"minifyx_prop_cssFilename";s:4:"type";s:9:"textfield";s:7:"options";a:0:{}s:5:"value";s:6:"styles";s:7:"lexicon";s:18:"minifyx:properties";s:4:"area";s:0:"";}s:10:"registerJs";a:7:{s:4:"name";s:10:"registerJs";s:4:"desc";s:23:"minifyx_prop_registerJs";s:4:"type";s:4:"list";s:7:"options";a:3:{i:0;a:2:{s:5:"value";s:11:"placeholder";s:4:"text";s:11:"Placeholder";}i:1;a:2:{s:5:"value";s:7:"startup";s:4:"text";s:14:"Startup script";}i:2;a:2:{s:5:"value";s:7:"default";s:4:"text";s:7:"Default";}}s:5:"value";s:11:"placeholder";s:7:"lexicon";s:18:"minifyx:properties";s:4:"area";s:0:"";}s:13:"jsPlaceholder";a:7:{s:4:"name";s: