(24, 0, 0, 'BabelLinks', 'Displays links to translated resources.', 0, 14, 0, '/**\n * Babel\n *\n * Copyright 2010 by Jakob Class <jakob.class@class-zec.de>\n *\n * This file is part of Babel.\n *\n * Babel is free software; you can redistribute it and/or modify it under the\n * terms of the GNU General Public License as published by the Free Software\n * Foundation; either version 2 of the License, or (at your option) any later\n * version.\n *\n * Babel is distributed in the hope that it will be useful, but WITHOUT ANY\n * WARRANTY; without even the implied warranty of MERCHANTABILITY or FITNESS FOR\n * A PARTICULAR PURPOSE. See the GNU General Public License for more details.\n *\n * You should have received a copy of the GNU General Public License along with\n * Babel; if not, write to the Free Software Foundation, Inc., 59 Temple Place,\n * Suite 330, Boston, MA 02111-1307 USA\n *\n * @package babel\n */\n/**\n * BabelLinks snippet to display links to translated resources\n *\n * Based on ideas of Sylvain Aerni <enzyms@gmail.com>\n *\n * @author Jakob Class <jakob.class@class-zec.de>\n *         goldsky <goldsky@virtudraft.com>\n *\n * @package babel\n *\n * @param resourceId        optional: id of resource of which links to translations should be displayed. Default: current resource\n * @param tpl               optional: Chunk to display a language link. Default: babelLink\n * @param activeCls         optional: CSS class name for the current active language. Default: active\n * @param showUnpublished   optional: flag whether to show unpublished translations. Default: 0\n * @param showCurrent       optional: flag whether to show a link to a translation of the current language. Default: 1\n */\n$babel = $modx->getService(''babel'', ''Babel'', $modx->getOption(''babel.core_path'', null, $modx->getOption(''core_path'').''components/babel/'').''model/babel/'', $scriptProperties);\n\n/* be sure babel and babel TV is loaded */\nif (!($babel instanceof Babel) || !$babel->babelTv)\n    return;\n\n/* get snippet properties */\n$resourceId = intval($modx->getOption(''resourceId'', $scriptProperties));\nif (empty($resourceId)) {\n    if (!empty($modx->resource) && is_object($modx->resource)) {\n        $resourceId = $modx->resource->get(''id'');\n    } else {\n        return;\n    }\n}\n$tpl              = $modx->getOption(''tpl'', $scriptProperties, ''babelLink'');\n$wrapperTpl       = $modx->getOption(''wrapperTpl'', $scriptProperties);\n$activeCls        = $modx->getOption(''activeCls'', $scriptProperties, ''active'');\n$showUnpublished  = $modx->getOption(''showUnpublished'', $scriptProperties, 0);\n$showCurrent      = $modx->getOption(''showCurrent'', $scriptProperties, 0);\n$outputSeparator  = $modx->getOption(''outputSeparator'', $scriptProperties, "\\n");\n$includeUnlinked  = $modx->getOption(''includeUnlinked'', $scriptProperties, 0);\n$ignoreSiteStatus = $modx->getOption(''ignoreSiteStatus'', $scriptProperties, 0);\n\nif (!empty($modx->resource) && is_object($modx->resource) && $resourceId === $modx->resource->get(''id'')) {\n    $contextKeys = $babel->getGroupContextKeys($modx->resource->get(''context_key''));\n    $resource    = $modx->resource;\n} else {\n    $resource = $modx->getObject(''modResource'', $resourceId);\n    if (!$resource) {\n        return;\n    }\n    $contextKeys = $babel->getGroupContextKeys($resource->get(''context_key''));\n}\n\n$linkedResources = $babel->getLinkedResources($resourceId);\n$languages       = $babel->getLanguages();\n$outputArray     = array();\nforeach ($contextKeys as $contextKey) {\n    if (!$showCurrent && $contextKey === $resource->get(''context_key'')) {\n        continue;\n    }\n    if (!$includeUnlinked && !isset($linkedResources[$contextKey])) {\n        continue;\n    }\n    $context = $modx->getObject(''modContext'', array(''key'' => $contextKey));\n    if (!$context) {\n        $modx->log(modX::LOG_LEVEL_ERROR, ''Could not load context: ''.$contextKey);\n        continue;\n    }\n    $context->prepare();\n    if (!$context->getOption(''site_status'', null, true) && !$ignoreSiteStatus) {\n        continue;\n    }\n    $cultureKey           = $context->getOption(''cultureKey'', $modx->getOption(''cultureKey''));\n    $translationAvailable = false;\n    if (isset($linkedResources[$contextKey])) {\n        $c = $modx->newQuery(''modResource'');\n        $c->where(array(\n            ''id''          => $linkedResources[$contextKey],\n            ''deleted:!=''  => 1,\n            ''published:='' => 1,\n        ));\n        if ($showUnpublished) {\n            $c->where(array(\n                ''OR:published:='' => 0,\n            ));\n        }\n        $count = $modx->getCount(''modResource'', $c);\n        if ($count) {\n            $translationAvailable = true;\n        }\n    }\n    $getRequest = $_GET;\n    unset($getRequest[''id'']);\n    unset($getRequest[$modx->getOption(''request_param_alias'', null, ''q'')]);\n    unset($getRequest[''cultureKey'']);\n    if ($translationAvailable) {\n        $url          = $context->makeUrl($linkedResources[$contextKey], $getRequest, ''full'');\n        $active       = ($resource->get(''context_key'') == $contextKey) ? $activeCls : '''';\n        $placeholders = array(\n            ''cultureKey'' => $cultureKey,\n            ''url''        => $url,\n            ''active''     => $active,\n            ''id''         => $linkedResources[$contextKey],\n            ''language''   => $languages[$cultureKey][''Description''],\n        );\n\n        if (!empty($toArray)) {\n            $outputArray[] = $placeholders;\n        } else {\n            $chunk = $babel->getChunk($tpl, $placeholders);\n            if (!empty($chunk)) {\n                $outputArray[] = $chunk;\n            }\n        }\n    } elseif ($includeUnlinked) {\n        $url          = $context->makeUrl($context->getOption(''site_start''), $getRequest, ''full'');\n        $active       = ($resource->get(''context_key'') == $contextKey) ? $activeCls : '''';\n        $placeholders = array(\n            ''cultureKey'' => $cultureKey,\n            ''url''        => $url,\n            ''active''     => $active,\n            ''id''         => $context->getOption(''site_start''),\n            ''language''   => $languages[$cultureKey][''Description''],\n        );\n\n        if (!empty($toArray)) {\n            $outputArray[] = $placeholders;\n        } else {\n            $chunk = $babel->getChunk($tpl, $placeholders);\n            if (!empty($chunk)) {\n                $outputArray[] = $chunk;\n            }\n        }\n    }\n}\n\nif (!empty($toArray)) {\n    return ''<pre>''.print_r($outputArray, 1).''</pre>'';\n}\n\n$output = implode($outputSeparator, $outputArray);\nif (!empty($wrapperTpl)) {\n    $output = $babel->getChunk($wrapperTpl, array(\n        ''babelLinks'' => $output\n    ));\n}\n\nif (!empty($toPlaceholder)) {\n    $modx->setPlaceholder($toPlaceholder, $output);\n    return;\n}\n\nreturn $output;', 0, 'a:6:{s:10:"resourceId";a:7:{s:4:"name";s:10:"resourceId";s:4:"desc";s:21:"babellinks.resourceId";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:0:"";s:7:"lexicon";s:16:"babel:properties";s:4:"area";s:0:"";}s:3:"tpl";a:7:{s:4:"name";s:3:"tpl";s:4:"desc";s:14:"babellinks.tpl";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:9:"babelLink";s:7:"lexicon";s:16:"babel:properties";s:4:"area";s:0:"";}s:9:"activeCls";a:7:{s:4:"name";s:9:"activeCls";s:4:"desc";s:20:"babellinks.activeCls";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:6:"active";s:7:"lexicon";s:16:"babel:properties";s:4:"area";s:0:"";}s:15:"showUnpublished";a:7:{s:4:"name";s:15:"showUnpublished";s:4:"desc";s:26:"babellinks.showUnpublished";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:1:"0";s:7:"lexicon";s:16:"babel:properties";s:4:"area";s:0:"";}s:11:"showCurrent";a:7:{s:4:"name";s:11:"showCurrent";s:4:"desc";s:22:"babellinks.showCurrent";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:1:"0";s:7:"lexicon";s:16:"babel:properties";s:4:"area";s:0:"";}s:15:"includeUnlinked";a:7:{s:4:"name";s:15:"includeUnlinked";s:4:"desc";s:26:"babellinks.includeUnlinked";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:1:"0";s:7:"lexicon";s:16:"babel:properties";s:4:"area";s:0:"";}}', '', 0, ''),
(25, 0, 0, 'BabelTranslation', 'Returns the id of a translated resource in a given context.', 0, 14, 0, '/**\n * Babel\n *\n * Copyright 2010 by Jakob Class <jakob.class@class-zec.de>\n *\n * This file is part of Babel.\n *\n * Babel is free software; you can redistribute it and/or modify it under the\n * terms of the GNU General Public License as published by the Free Software\n * Foundation; either version 2 of the License, or (at your option) any later\n * version.\n *\n * Babel is distributed in the hope that it will be useful, but WITHOUT ANY\n * WARRANTY; without even the implied warranty of MERCHANTABILITY or FITNESS FOR\n * A PARTICULAR PURPOSE. See the GNU General Public License for more details.\n *\n * You should have received a copy of the GNU General Public License along with\n * Babel; if not, write to the Free Software Foundation, Inc., 59 Temple Place,\n * Suite 330, Boston, MA 02111-1307 USA\n *\n * @package babel\n */\n/**\n * BabelTranslation snippet to get the id of a translated resource in a given context.\n *\n * @author Jakob Class <jakob.class@class-zec.de>\n *\n * @package babel\n *\n * @param resourceId		optional: id of resource of which a translated resource should be determined. Default: current resource\n * @param contextKey		optional: Key of context in which translated resource should be determined.\n * @param cultureKey		optional: Key of culture in which translated resource should be determined. Used only in case contextKey was not specified.  If both omitted: uses currently set cultureKey.\n * @param showUnpublished	optional: flag whether to show unpublished translations. Default: 0\n */\n$babel = $modx->getService(''babel'', ''Babel'', $modx->getOption(''babel.core_path'', null, $modx->getOption(''core_path'').''components/babel/'').''model/babel/'', $scriptProperties);\n\n/* be sure babel and babel TV is loaded */\nif (!($babel instanceof Babel) || !$babel->babelTv)\n    return;\n\n/* get snippet properties */\n$resourceId = intval($modx->getOption(''resourceId'', $scriptProperties));\nif (empty($resourceId)) {\n    if (!empty($modx->resource) && is_object($modx->resource)) {\n        $resourceId = $modx->resource->get(''id'');\n    } else {\n        return;\n    }\n}\n$contextKey = $modx->getOption(''contextKey'', $scriptProperties, '''', true);\nif (empty($contextKey)) {\n    $cultureKey = $modx->getOption(''cultureKey'', $scriptProperties, '''', true);\n    $contextKey = $babel->getContextKey($cultureKey);\n}\n$showUnpublished = $modx->getOption(''showUnpublished'', $scriptProperties, 0, true);\n\n/* determine id of tranlated resource */\n$linkedResources = $babel->getLinkedResources($resourceId);\n$output          = null;\nif (isset($linkedResources[$contextKey])) {\n    $resource = $modx->getObject(''modResource'', $linkedResources[$contextKey]);\n    if ($resource && ($showUnpublished || $resource->get(''published'') == 1)) {\n        $output = $resource->get(''id'');\n    }\n}\nreturn $output;', 0, 'a:4:{s:10:"resourceId";a:7:{s:4:"name";s:10:"resourceId";s:4:"desc";s:27:"babeltranslation.resourceId";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:0:"";s:7:"lexicon";s:16:"babel:properties";s:4:"area";s:0:"";}s:10:"contextKey";a:7:{s:4:"name";s:10:"contextKey";s:4:"desc";s:27:"babeltranslation.contextKey";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:0:"";s:7:"lexicon";s:16:"babel:properties";s:4:"area";s:0:"";}s:10:"cultureKey";a:7:{s:4:"name";s:10:"cultureKey";s:4:"desc";s:27:"babeltranslation.cultureKey";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:0:"";s:7:"lexicon";s:16:"babel:properties";s:4:"area";s:0:"";}s:15:"showUnpublished";a:7:{s:4:"name";s:15:"showUnpublished";s:4:"desc";s:32:"babeltranslation.showUnpublished";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:1:"0";s:7:"lexicon";s:16:"babel:properties";s:4:"area";s:0:"";}}', '', 0, ''),
(26, 0, 0, 'tagLister', 'A simple tag listing snippet that grabs tags from a TV value.', 0, 15, 0, '/**\n * tagLister\n *\n * Copyright 2010 by Shaun McCormick <shaun@modxcms.com>\n *\n * This file is part of tagLister, a simple tag listing snippet for MODx\n * Revolution.\n *\n * tagLister is free software; you can redistribute it and/or modify it under\n * the terms of the GNU General Public License as published by the Free Software\n * Foundation; either version 2 of the License, or (at your option) any later\n * version.\n *\n * tagLister is distributed in the hope that it will be useful, but WITHOUT\n * ANY WARRANTY; without even the implied warranty of MERCHANTABILITY or FITNESS\n * FOR A PARTICULAR PURPOSE. See the GNU General Public License for more\n * details.\n *\n * You should have received a copy of the GNU General Public License along with\n * tagLister; if not, write to the Free Software Foundation, Inc., 59 Temple\n * Place, Suite 330, Boston, MA 02111-1307 USA\n *\n * @package taglister\n */\n/**\n * tagLister snippet\n *\n * @var modX $modx\n * @var TagLister $tagLister\n * @var array $scriptProperties\n *\n * @package taglister\n */\n$tagLister = $modx->getService(''taglister'',''TagLister'',$modx->getOption(''taglister.core_path'',null,$modx->getOption(''core_path'').''components/taglister/'').''model/taglister/'',$scriptProperties);\nif (!($tagLister instanceof TagLister)) return '''';\n$modx->lexicon->load(''taglister:default'');\n\n/* setup default properties */\n$tpl = $modx->getOption(''tpl'',$scriptProperties,''tag'');\n$tv = $modx->getOption(''tv'',$scriptProperties,''tags'');\n$tvDelimiter = $modx->getOption(''tvDelimiter'',$scriptProperties,'','');\n$target = $modx->getOption(''target'',$scriptProperties,1);\n$tagVar = $modx->getOption(''tagVar'',$scriptProperties,''tag'');\n$tagKeyVar = $modx->getOption(''tagKeyVar'',$scriptProperties,''key'');\n$limit = $modx->getOption(''limit'',$scriptProperties,10);\n$sortBy = strtolower($modx->getOption(''sortBy'',$scriptProperties,''count''));\n$sortDir = strtoupper($modx->getOption(''sortDir'',$scriptProperties,''ASC''));\n$cls = $modx->getOption(''cls'',$scriptProperties,'''');\n$altCls = $modx->getOption(''altCls'',$scriptProperties,'''');\n$firstCls = $modx->getOption(''firstCls'',$scriptProperties,'''');\n$lastCls = $modx->getOption(''lastCls'',$scriptProperties,'''');\n$activeCls = $modx->getOption(''activeCls'',$scriptProperties,'''');\n$activeTag = isset($_REQUEST[$tagVar]) ? $modx->stripTags(urldecode($_REQUEST[$tagVar])) : '''';\n$activeKey = isset($_REQUEST[$tagKeyVar]) ? $modx->stripTags(urldecode($_REQUEST[$tagKeyVar])) : '''';\n$all = $modx->getOption(''all'',$scriptProperties,false);\n$toLower = $modx->getOption(''toLower'',$scriptProperties,false);\n$weights = $modx->getOption(''weights'',$scriptProperties,0);\n$weightCls = $modx->getOption(''weightCls'',$scriptProperties,'''');\n$useTagFurl = $modx->getOption(''useTagFurl'',$scriptProperties,false);\n$furlKey = $modx->getOption(''furlKey'',$scriptProperties,''tags'');\n\n\n/* get TV values */\n$c = $modx->newQuery(''modTemplateVarResource'');\n$c->innerJoin(''modTemplateVar'',''TemplateVar'');\n$c->innerJoin(''modResource'',''Resource'');\n$c->leftJoin(''modUser'',''CreatedBy'',''CreatedBy.id = Resource.createdby'');\n$c->leftJoin(''modUser'',''PublishedBy'',''PublishedBy.id = Resource.publishedby'');\n$c->leftJoin(''modUser'',''EditedBy'',''EditedBy.id = Resource.editedby'');\n$tvPk = (int)$tv;\nif (!empty($tvPk)) {\n    $c->where(array(''TemplateVar.id'' => $tvPk));\n} else {\n    $c->where(array(''TemplateVar.name'' => $tv));\n}\n/* parents support */\n$parents = isset($parents) ? explode('','', $parents) : array();\nif (!empty($parents)) {\n    $depth = isset($depth) ? (integer) $depth : 10;\n    $children = array();\n    foreach ($parents as $parent) {\n        $kids = $modx->getChildIds($parent,$depth);\n        if (!empty($kids)) {\n            $children = array_merge($children,$kids);\n        }\n    }\n    if (!empty($children)) {\n        $children = array_unique($children);\n        $parents = array_merge($parents,$children);\n    }\n    $parents = array_unique($parents);\n    if (!empty($parents)) {\n        $c->where(array(\n            ''Resource.id:IN'' => $parents,\n        ));\n    }\n}\nif (!$modx->getOption(''includeDeleted'',$scriptProperties,false)) {\n    $c->where(array(''Resource.deleted'' => 0));\n}\nif (!$modx->getOption(''includeUnpublished'',$scriptProperties,false)) {\n    $c->where(array(''Resource.published'' => 1));\n}\n/* json where support */\n$where = $modx->getOption(''where'',$scriptProperties,'''');\nif (!empty($where)) {\n    $where = $modx->fromJSON($where);\n    if (is_array($where) && !empty($where)) {\n        $c->where($where);\n    }\n}\nif ($sortBy == ''publishedon'') {\n    $c->sortby(''Resource.publishedon'',$sortDir);\n} else if (in_array($sortBy,array(''rand'',''random'',''rand()''))) {\n    $c->sortby(''RAND()'','''');\n}\n$tags = $modx->getCollection(''modTemplateVarResource'',$c);\n\n/* parse TV values */\n$output = array();\n$tagList = array();\n$encoding = $modx->getOption(''modx_charset'',$scriptProperties,''UTF-8'');\n$useMultibyte = $modx->getOption(''use_multibyte'',$scriptProperties,false);\n/** @var modTemplateVarResource $tag */\nforeach ($tags as $tag) {\n   $v = $tag->get(''value'');\n   $vs = explode($tvDelimiter,$v);\n   foreach ($vs as $key) {\n      $key = trim($key);\n      if (empty($key)) continue;\n      if ($toLower) { /* allow for case-insensitive filtering */\n          $key = $useMultibyte ? mb_strtolower($key,$encoding) : strtolower($key);\n      }\n      /* increment tag count */\n      if (empty($tagList[$key])) {\n         $tagList[$key] = 1;\n      } else { $tagList[$key]++; }\n   }\n}\n\n/* sort */\nswitch ($sortBy.''-''.$sortDir) {\n    case ''publishedon-DESC'': case ''publishedon-ASC'': break;\n    case ''tag-ASC'': ksort($tagList); break;\n    case ''tag-DESC'': krsort($tagList); break;\n    case ''count-DESC'': asort($tagList); break;\n    case ''count-ASC'': default: arsort($tagList); break;\n    case ''rand-ASC'': case ''random-ASC'': case ''rand()-asc'': $tagList = $tagLister->ashuffle($tagList); break;\n}\n\n/* iterate */\n$totalTags = 0;\n$i = $all ? 1 : 0;\nforeach ($tagList as $tag => $count) {\n    if ($i >= $limit) break;\n    $tagCls = $cls.((!empty($altCls) && $i % 2)? '' ''.$altCls : '''');\n    if (!empty($firstCls) && $i == 0) $tagCls .= '' ''.$firstCls;\n    if (!empty($lastCls) && ($i+1 >= $limit || $i == $count)) $tagCls .= '' ''.$lastCls;\n    /* if tag is currently being viewed, mark as active */\n    if (!empty($activeCls) && $tag==$activeTag && (empty($activeKey) || $tv==$activeKey)) $tagCls .= '' ''.$activeCls;\n    /* handle weighting for css */\n    if (!empty($weights) && !empty($weightCls)) $tagCls .= '' ''.$weightCls.ceil($count / (max($tagList) / $weights));\n\n    $tagArray = array(\n        ''tag'' => $tag,\n        ''tagVar'' => $tagVar,\n        ''tagKey'' => $tv,\n        ''tagKeyVar'' => $tagKeyVar,\n        ''count'' => $count,\n        ''target'' => $target,\n        ''cls'' => $tagCls,\n        ''idx'' => $i,\n    );\n    $tagParams = array();\n    if (empty($useTagFurl)) {\n        $tagParams[$tagVar] = $tag;\n        $tagParams[$tagKeyVar] = $tv;\n    }\n    $tagArray[''url''] = $modx->makeUrl($target,'''',$tagParams);\n    if (!empty($useTagFurl)) {\n        $tagArray[''url''] = rtrim($tagArray[''url''],''/'').''/''.(!empty($furlKey) ? $furlKey : $tv).''/''.urlencode($tag);\n    }\n\n    $output[] = $tagLister->getChunk($tpl,$tagArray);\n    $totalTags += $count;\n    $i++;\n}\n\nif ($all) {\n    $allTpl = $modx->getOption(''allTpl'',$scriptProperties,''all'');\n    $allChunk = $tagLister->getChunk($allTpl,array(\n        ''tag'' => !empty($scriptProperties[''allText'']) ? $scriptProperties[''allText''] : $modx->lexicon(''all_tags''),\n        ''tagVar'' => $tagVar,\n        ''tagKey'' =>