if string provided */\nif (!is_numeric($priorityTV)) {\n    \n    $c = $modx->newQuery(''modTemplateVar'');\n    $c->select(''id'');\n    $c->where(array(''name'' => $priorityTV));\n    $priorityTV = $modx->getValue($c->prepare());\n    \n}\n\n/* Query by Context and set site_url / site_start */\n$items = '''';\n// Set today''s date for homepage lastmod\n$today = date(''Y-m-d'');\nforeach ($context as $ctx) {\n    \n    $siteUrl = '''';\n    // Fetch current context object for site_url\n    $currentCtx = $modx->getContext($ctx);\n    if ($currentCtx) {\n        $siteUrl = $currentCtx->getOption(''site_url'');\n        // Add site_url to output\n        $items .= "<url><loc>{$siteUrl}</loc><lastmod>{$today}</lastmod></url>" . PHP_EOL;\n    } \n    if (empty($siteUrl)) {\n        // We need something to build the links with, even if no context setting\n        $siteUrl = $modx->getOption(''site_url'', null, MODX_SITE_URL);\n    }\n    \n    $tablePrefix = $modx->getOption(''table_prefix'');\n    \n    $filters[] = "s.context_key = ''{$ctx}''";\n    $criteria = implode('' AND '', $filters);\n    $tvQuery = '''';\n    if ($priorityTV) $tvQuery = "IFNULL(\n        CONCAT(''<priority>'',(\n            SELECT value\n            FROM " . $tablePrefix . "site_tmplvar_contentvalues\n            USE INDEX (tv_cnt)\n            WHERE contentid = s.id AND tmplvarid = " . $priorityTV . "\n        ),''</priority>''),''''),";\n        \n    // Add all resources that meet criteria\n    $stmt = $modx->query("\n        SELECT\n    	    GROUP_CONCAT(\n                ''<url>'',        \n                CONCAT(''<loc>" . $siteUrl . "'',uri,''</loc>''),\n                CONCAT(''<lastmod>'',CASE editedon WHEN 0 THEN FROM_UNIXTIME(publishedon, ''%Y-%m-%d'') ELSE FROM_UNIXTIME(editedon, ''%Y-%m-%d'') END,''</lastmod>''),\n                " . $tvQuery . "\n                ''</url>''\n                SEPARATOR ''''\n            ) AS node\n        FROM " . $tablePrefix . "site_content AS s\n        WHERE " . $criteria . "\n        GROUP BY s.id\n        ORDER BY " . $orderby . "\n    ");\n\n    // Add to output\n    if ($stmt) {\n        $rows = $stmt->fetchAll(PDO::FETCH_COLUMN);\n        $items .= implode(PHP_EOL, $rows);\n    }\n    \n}\n\n/* get container tpl and content */\n$output = $modx->getChunk($containerTpl, array(\n    ''schema'' => $googleSchema,\n    ''items'' => $items,\n));\n\nif ($output !== null) {\n    $modx->cacheManager->set($cacheKey, $output, $expires, $options);\n    return $output;\n} else {\n    $modx->log(modX::LOG_LEVEL_WARN, ''GoogleSiteMap could not generate nor fetch a sitemap.'');\n}', 0, 'a:15:{s:11:"cachePrefix";a:7:{s:4:"name";s:11:"cachePrefix";s:4:"desc";s:25:"prop_gsm.cacheprefix_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:13:"googlesitemap";s:7:"lexicon";s:24:"googlesitemap:properties";s:4:"area";s:0:"";}s:14:"cachePartition";a:7:{s:4:"name";s:14:"cachePartition";s:4:"desc";s:28:"prop_gsm.cachepartition_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:13:"googlesitemap";s:7:"lexicon";s:24:"googlesitemap:properties";s:4:"area";s:0:"";}s:12:"cacheExpires";a:7:{s:4:"name";s:12:"cacheExpires";s:4:"desc";s:26:"prop_gsm.cacheexpires_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:5:"86400";s:7:"lexicon";s:24:"googlesitemap:properties";s:4:"area";s:0:"";}s:7:"context";a:7:{s:4:"name";s:7:"context";s:4:"desc";s:21:"prop_gsm.context_desc";s:4:"type";s:9:"textfield";s:7:"options";s:0:"";s:5:"value";s:0:"";s:7:"lexicon";s:24:"googlesitemap:properties";s:4:"area";s:0:"";}s:11:"hideDeleted";a:7:{s:4:"name";s:11:"hideDeleted";s:4:"desc";s:25:"prop_gsm.hidedeleted_desc";s:4:"type";s:13:"combo-boolean";s:7:"options";s:0:"";s:5:"value";b:1;s:7:"lexicon";s:24:"googlesitemap:properties";s:4:"area";s:0:"";}s:10:"showHidden";a:7:{s:4:"name";s:10:"showHidden";s:4:"desc";s:24:"prop_gsm.showhidden_desc";s:4:"type";s:13:"combo-boolean";s:7:"options";s:0:"";s:5:"value";b:0;s:7:"